const Qoo = {
    get:()=>{
        console.log('取出Qoo一瓶');
    },
    add:()=>{
        console.log('補給Qoo一瓶');
    }
}

// 外部取用時，會得到QooDrink物件 + 裡面的屬性與方法
// 若單個object內有多個屬性與方法，可藉由此種方式一次expose到外部
// 偏向打包外帶的概念
exports.QooDrink = Qoo;


// 外部引用時可以直接使用物件內的屬性與內容
// 該方式被外部引用時效果等同下方拆分後一個一個exports的寫法
// 等同直接取用Qoo內的內容
module.exports = Qoo;

